mapper.update:
	update userinfo
	set user_phone = #{userPhone}, user_sex = #{userSex}, user_address = #{userAddress},
    	user_career = #{userCareer}, user_age = #{userAge}, user_final_education = #{userFinalEducation},
    	user_language = #{userLanguage}, user_skills = #{userSkills}, user_image = #{userImage},
    	sns_type = #{snsType}, sns_id = #{snsId}, job = #{job}
	where user_seq = #{userSeq}
